import React, { Component } from "react";
import "./homeSection.css";

// -----------------------------------------------------------------------
import { connect } from "react-redux";
import { createSelector } from "reselect";
import { passTarget } from "../../../action/SetTargetA";

class HomeSection extends Component {
  contactRef = React.createRef();
  componentDidMount() {
    // this.propsonPassTarget(this.contactRef);
  }
  render() {
    let {
      isFirst,
      isLast,
      heading,
      className,
      contentName,
      imgIs4,
      isContent4,
      id
    } = this.props;

    return (
      <section
        ref={contentName === "contact-content" ? this.contactRef : ""}
        className={"columns" + (className ? " " + className : "")}
        id={id ? id : ""}
      >
        {isFirst ? (
          <div className={"column img" + (imgIs4 ? " is-4" : "")} />
        ) : (
          ""
        )}
        <div
          className={
            "column" +
            (contentName ? " " + contentName : "") +
            (isContent4
              ? " is-4-desktop is-4-tablet is-8-mobile vertical-center"
              : "")
          }
        >
          {contentName === "contact-content" ? (
            this.props.children
          ) : (
            <div>
              <h1
                className={
                  "title is-1" + (heading === "Hello." ? "" : " is-uppercase")
                }
              >
                {heading}
              </h1>
              {this.props.children}
            </div>
          )}
        </div>
        {isLast ? (
          <div className={"column img" + (imgIs4 ? " is-4" : "")} />
        ) : (
          ""
        )}
      </section>
    );
  }
}

const Selector = createSelector(state => state.target, target => target);

const mapStateToProps = state => {
  console.log(state);
  return { target: Selector(state) };
};

const mapActionToProps = {
  onPassTarget: passTarget
};

export default connect(
  mapStateToProps,
  mapActionToProps
)(HomeSection);
