import React, { Component, Fragment } from "react";
// import "./App.css";
import Footer from "./component/footer/Footer";
// import Home from "./component/home/Home";
import Navbar from "./component/navbar/navbar";
import ProductPage from "./component/product-page/ProductPage";
import Facilities from "./component/facilities-page/Facilities";
import Events from "./component/events-page/Events";
class App extends Component {
  state = {
    navbarCLass: ""
  };
  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = event => {
    if (document.body.scrollTop || document.documentElement.scrollTop) {
      this.setState({ navbarCLass: "navbar-mh border" });
    } else {
      this.setState({ navbarCLass: "" });
    }
  };

  render() {
    return (
      <Fragment>
        <Navbar className={this.state.navbarCLass} />
        <main>
          {/* <Home /> */}
          {/* <Facilities /> */}
          <Events />
          {/* <ProductPage /> */}
        </main>
        <Footer />
        <div className="modal" />
      </Fragment>
    );
  }
}

export default App;
