import React from "react";
import OverviewDataLeft from "./overviewData/OverviewDataLeft";
import OverviewDataRight from "./overviewData/OverviewDataRight";
import "./overview.css";
export default function Overview(props) {
  var { title, titleClass, titleClass } = props;
  return (
    <div className="facilities-overview">
      <h1
        className={
          "h1 title is-uppercase" + (titleClass ? " " + titleClass : "")
        }
      >
        {title}
      </h1>
      <div className="facilities-overview-content columns">
        <OverviewDataLeft>
          <p className="overview-data">
            With our head office in Mumbai, the commercial capital of India, our
            manufacturing and dyeing facilities are located in Silvassa and Vapi
            respectively, 160 kms north of Mumbai.
          </p>
          <p className="overview-data">
            Silvassa houses SPFLâ€™s 2 manufacturing units, each near the other,
            as and when they were established in succession. These are
            vertically integrated plants which allow us to produce more than 250
            different varieties of yarns and threads to satisfy the different
            market demands while maintaining the highest levels of production
            and quality standards.
          </p>
          <p className="overview-data">
            Each unit is designed to be a stand-alone facility to ensure
            continued service to our customers in case of any natural or manmade
            calamity.
          </p>
        </OverviewDataLeft>
        <OverviewDataRight />
      </div>
    </div>
  );
}
